import TimeInterval from './time-interval.js';
import { durationDay, durationMinute } from '../../toolbox/src/index';

const day = new TimeInterval(
  'day',
  d => d.setHours(0, 0, 0, 0),
  (d, s) => d.setDate(d.getDate() + s),
  (s, e) =>
    (e - s - (e.getTimezoneOffset() - s.getTimezoneOffset()) * durationMinute) / durationDay,
  d => d.getDate() - 1
);

export default day;
