import TimeInterval from './time-interval.js';

const month = new TimeInterval(
  'month',
  d => {
    d.setDate(1);
    d.setHours(0, 0, 0, 0);
  },
  (d, s) => d.setMonth(d.getMonth() + s),
  (s, e) => (e.getMonth() - s.getMonth()) + ((e.getFullYear() - s.getFullYear()) * 12),
  d => d.getMonth()
);

export default month;
