import TimeInterval from '../time-interval.js';
import { durationDay } from '../../../toolbox/src/index';

const utcDay = new TimeInterval(
  'day',
  d => d.setUTCHours(0, 0, 0, 0),
  (d, s) => d.setUTCDate(d.getUTCDate() + s),
  (s, e) => (e - s) / durationDay,
  d => d.getUTCDate() - 1
);

export default utcDay;
