import TimeInterval from '../time-interval.js';
import { durationHour } from '../../../toolbox/src/index';

const utcHour = new TimeInterval(
  'hour',
  d => d.setUTCMinutes(0, 0, 0),
  (d, s) => d.setTime(Number(d) + s * durationHour),
  (s, e) => (e - s) / durationHour,
  d => d.getUTCHours()
);

export default utcHour;
