import TimeInterval from '../time-interval.js';
import { durationMinute } from '../../../toolbox/src/index';

const utcMinute = new TimeInterval(
  'minute',
  d => d.setUTCSeconds(0, 0),
  (d, s) => d.setTime(Number(d) + s * durationMinute),
  (s, e) => (e - s) / durationMinute,
  d => d.getUTCMinutes()
);

export default utcMinute;
