import UtcYearInterval from './time-interval-utcYear.js';

var utcYear = new UtcYearInterval(
  'year',
  d => {
    d.setUTCMonth(0, 1);
    d.setUTCHours(0, 0, 0, 0);
  },
  (d, s) => d.setUTCFullYear(d.getUTCFullYear() + s),
  (s, e) => e.getUTCFullYear() - s.getUTCFullYear(),
  d => d.getUTCFullYear()
);

export default utcYear;
