function filterSelectedData(data, valueIndex, filters){
  if(!filters.length){
    return [];
  }
  return data.filter((row)=>{
    if(filters.includes(row[valueIndex])){
      return row;
    }
    return false;
  });
}

function getUniqueValues(data, key){
  let uniqueValues = [];
  if(key > -1){
    data.map((row)=>{
      if(!uniqueValues.includes(row[key])){
        uniqueValues.push(row[key]);
      }
      return true;
    });
  }
  return uniqueValues;
}

export { filterSelectedData, getUniqueValues };