import DataStore from '../../src/datastore';
import cityBikesData from '../../sample/data/cityBikes';
import cityBikesSchema from '../../sample/data/cityBikes.schema';
import { indexColBetween, groupBy } from '../../src/operators';
import { DatetimeUnits } from '../../src/toolbox/src/index';

let dataStore = new DataStore(cityBikesData, cityBikesSchema, { enabledIndex: false, indexBy: 'starttime' }),
  dataTable = dataStore.getDataTable();

dataTable.query([
  indexColBetween(0, 1391270700000, 1391326080000),
  groupBy([{
    column: 'starttime',
    timeUnit: DatetimeUnits.Hour
  }],
  [{
    column: 'tripduration',
    operation: 'avg',
    outputAs: 'total trip duration'
  }])
]);

