import { writable } from 'svelte/store';
import { LayoutObject, VizRecordDomainConfig, PaginationState, GridDimensions, Utils } from '../globals/helpers/helpers';

function createStores(){
  const numCards = writable(1),
    autoHeight = writable(false),
    vizRecordDomain = writable(<VizRecordDomainConfig>{}),
    paginationState = writable(<PaginationState>{}),
    layoutObject = writable(<LayoutObject>{}),
    gridDimensions = writable(<GridDimensions>{}),
    headerSelectedState = writable(false),
    visualUtils = writable(<Utils>{}),
    observer = writable(),
    hasRendered = writable(false),
    hasCreditLabel = writable(true),
    creditLabelElement = writable(null),
    colSearchValues = writable([]),
    filterIndex = writable(-1),
    conditionalFilterValues = writable([]),
    conditionalFilterLogicalOperators = writable([]),
    valueFilterData = writable([]);
  return { 
    numCards,
    autoHeight,
    vizRecordDomain,
    paginationState,
    layoutObject,
    gridDimensions,
    headerSelectedState,
    visualUtils,
    observer,
    hasRendered,
    hasCreditLabel,
    colSearchValues,
    filterIndex,
    conditionalFilterValues,
    conditionalFilterLogicalOperators,
    valueFilterData,
    creditLabelElement
  };
}
export default createStores;