import FusionGrid from './public-api/grid';
// @ts-ignore
import Redloum from './decryption/redluom';
import { version, releaseDate } from '../package.json';
// @ts-ignore
import DataStore from '@fusiongrid/datastore';

// @ts-ignore
FusionGrid.DataStore = DataStore;
FusionGrid.items = [];
FusionGrid.options = {};
FusionGrid.versionDetails = [];

FusionGrid.versionDetails.push(version.split('.'));
FusionGrid.versionDetails.push(new Date(releaseDate).getTime());

function license (licenseObj: any) {
  let key = licenseObj.key;
  if(key){
    Redloum.storeObj(FusionGrid, key);
  }
} 

// @ts-ignore
FusionGrid.options.license = license;
// @ts-ignore
window.FusionGrid = FusionGrid;
export default FusionGrid;
