import { ColumnType } from '../../src/public-api/interfaces';

/**
 * Get type of grid column based on the column type in DataStore schema
 * @param colType Column type in DataStore schema
 */
function deriveColumnTypeFromSchema(colType: string): ColumnType {
  if (typeof colType === 'undefined') {
    return ColumnType.String;
  }

  switch (colType) {
    case 'datetime': 
      return ColumnType.DateTime;
    case 'number':
      return ColumnType.Number;
    default:
      return ColumnType.String;
  }
}

export { deriveColumnTypeFromSchema };