const nav = window.navigator,
  isIE11: boolean = /trident/i.test(nav.userAgent) && /rv:11/i.test(nav.userAgent),
  isEdge: boolean =  /Edg/.test(nav.userAgent),
  isIOS: boolean = /iPad|iPhone|iPod/.test(nav.platform) ||
    (nav.platform === 'MacIntel' && nav.maxTouchPoints > 1);

export { isIE11, isEdge, isIOS };