import { isNumber } from '../type-utils';

let numberComparator = (a: number, b: number) => {
    if (a === null || typeof a === 'undefined') {
      return b === a ? 0 : 1;
    } else if (isNumber(b)) {
      return a - b;
    } else if ((isNaN(a) && isNaN(b))) {
      return 0;
    }
    return -1;
  }, stringComparator = (a: unknown, b: unknown) => {
    if (a === null || typeof a === 'undefined') {
      return b === a ? 0 : 1;
    } else if (isNumber(b)) {
      return isNumber(a) ? <number>a - <number>b : -1;
    }
    return a === b ? 0 : <string>a < <string>b ? -1 : 1;
  };

export { numberComparator, stringComparator };