/**
 * Returns true if the provided parameter is a number
 *
 * @param {*} x The parameter to be checked
 *
 * @return {Boolean}
 */
const isNumber = (x: unknown) => typeof x === 'number' && !isNaN(x - x),
  /**
   * Returns true if the provided value is a natural number. Natural number is non zero positive whole number only.
   * @param {*} x The value to be checked
   * @returns {Boolean} true if the value is a natural number
   */
  isNaturalNumber = (x: unknown) => typeof x === 'number' && !isNaN(x - x) && x > 0 && Math.floor(x) === x,
  /**
   * Returns true if the provided value is a whole number. Whole number is zero, positive number only.
   * @param {*} x The value to be checked
   * @returns {Boolean} true if the value is a whole number
   */
  isWholeNumber = (x: unknown) => typeof x === 'number' && !isNaN(x - x) && x >= 0 && Math.floor(x) === x;

export { isNumber, isNaturalNumber, isWholeNumber };