let counter = 0;

/**
 * Generates a unique ID prepended with an optional prefix.
 *
 * @param {string} [prefix = ''] An optional prefix which will be prepended
 * before the generated unique ID.
 *
 * @returns {string} A unique ID
 */
const uid = (prefix = '') => prefix + '' + counter++,
  /**
   * Generate unique identity fields which gives serial values
   * @param {number} [start] An optional start value for identity values
   * @returns {number} unique identity
   */
  numberIdentity = (start: number = 1) => {
    let unique_id = start;
    return () => unique_id++;
  };

export default uid;
export { numberIdentity };
