import { getSpanDimension } from '../src/units/dom-dimensions';

describe('dimension check for strings', () => {
  let dummyParentDiv = document.createElement('div'), 
    dim = getSpanDimension(document, dummyParentDiv, {});
  it('For any particular text, it returns calculated width and height', (done) => {
    expect(dim.width).toBeDefined();
    expect(dim.height).toBeDefined();
    done();
  });
});