import { parseLengthUnit } from '../src/units/parse-length';
let lengthUnitObj = {
  em: 16,
  hPercentage: 6,
  pt: 1,
  px: 1,
  rem: 16,
  wPercentage: 14
};
describe('parseLengthUnit unit tests', () => {
  let lengthGenerated;
  //px, pt, horizntal value, vertical value, em, rem length calcluated
  it('length units converts px length units successfully', (done) => {
    lengthGenerated = parseLengthUnit('22px', lengthUnitObj, 'px');
    expect(lengthGenerated).toBe(22);
    done();
  });
  it('length units converts pt length units successfully', (done) => {
    lengthGenerated = parseLengthUnit('22pt', lengthUnitObj, 'pt');
    expect(lengthGenerated).toBe(22);
    done();
  });
  it('length units converts em length units successfully', (done) => {
    lengthGenerated = parseLengthUnit('22em', lengthUnitObj, 'em');
    expect(lengthGenerated).toBe(352);
    done();
  });
  it('length units converts rem length units successfully', (done) => {
    lengthGenerated = parseLengthUnit('22rem', lengthUnitObj, 'rem');
    expect(lengthGenerated).toBe(352);
    done();
  });
  it('length units converts horizontal percentage length units successfully', (done) => {
    lengthGenerated = parseLengthUnit('22%', lengthUnitObj, 'height');
    expect(lengthGenerated).toBe(132);
    done();
  });
  it('length units converts vertical percentage length units successfully', (done) => {
    lengthGenerated = parseLengthUnit('22%', lengthUnitObj, 'width');
    expect(lengthGenerated).toBe(308);
    done();
  });
});