        containerStyle = "height: 14px;width: 14px;position: relative;display: inline-block;vertical-align: bottom;",
        textStyle = "width: 88px;height: 12px;flex-grow: 0;font-family: 'Source Sans Pro';font-size: 12px;font-weight: bold;font-stretch: normal;font-style: normal;line-height: 1;letter-spacing: 0.3px;text-align: left;color: #60728b;",
        trialTextStyle = "font-weight: 500;",
        shapeStyle = "width: 2px;height: 2px;background-color: #60728b;position: absolute;",
        creditLabelElement;


    hasCreditLabel.subscribe((newValue) => {
        drawCreditLabel = newValue;
    })

    onMount(() => {
        stores.creditLabelElement.set(creditLabelElement);
    })
</script>
{#if drawCreditLabel}
<div on:click={handleCreditLabelClick} style={wrapperStyle} bind:this={creditLabelElement}>
