        <div style={`${shapeStyle}margin-left: 4px;`}></div>
        <div style={`${shapeStyle}margin-left: 8px;`}></div>
        <div style={`${shapeStyle}margin-top: 4px;`}></div>
        <div style={`${shapeStyle}margin-left: 4px;margin-top: 4px;`}></div>
        <div style={`${shapeStyle}margin-left: 8px;margin-top: 4px;`}></div>
        <div style={`${shapeStyle}margin-top: 8px;`}></div>
        <div style={`${shapeStyle}margin-left: 4px;margin-top: 8px;`}></div>
