<script>
    import First from './first.svelte';
    import Last from './last.svelte';
    import Next from './next.svelte';
    import Previous from './previous.svelte';
    import PageDetails from './page-details.svelte';

    export let config;
    export let width;

    const ALL_BUTTONS_WIDTH = 192.8,
        INPUT_WITH_SINGLE_JUMP_ARROWS_WIDTH = 123;
    
    let drawAllButtons = ALL_BUTTONS_WIDTH <= width;
</script> 
<style>
</style>
{#if config.showJumptoFirstPage && drawAllButtons}
    <div class="fg-pagination-first-page-button">
        <First {config}/>
    </div>
{/if}
<div class="fg-pagination-previous-button" style="width: 8px; height: 13px;">
    <Previous {config}/>
</div>
{#if config.showPages.enable && (drawAllButtons || (INPUT_WITH_SINGLE_JUMP_ARROWS_WIDTH <= width))}
    <div class="fg-pagination-page-details">
        <PageDetails {config} displayTotal={drawAllButtons}/>
    </div>
{/if}
<div class="fg-pagination-next-button" style="width: 8px; height: 13px;">
    <Next {config}/>
</div>
{#if config.showJumpToLastPage && drawAllButtons}
    <div class="fg-pagination-last-page-button">
        <Last {config}/>
    </div>
{/if}
 