<script>
    import { getContext } from 'svelte';
    export let config;
    export let displayTotal;

    let handlers = getContext('handlers');

    function handleUserInput(e){
        if (e.keyCode === 13){
            handlers.jumpToPage(e.target.value);
        }
    }

</script>

{#if config.showPages.userInput}
    <input on:keydown={handleUserInput} type="text" style="width: 31px; height: 25px; margin-right: { config.showPages.showTotal ? 5: 0}px" value={config.currentPage}/>
{:else}
    <div class="fg-pagination-current-page">{config.currentPage}</div>
{/if} 
{#if config.showPages.showTotal && displayTotal}    
    <div class="fg-pagination-total-page"> of {config.totalPages}</div>
{/if}
