<script>
    import { getContext } from 'svelte';

    export let options;
    export let pageSize;

    let handlers = getContext('handlers');
    
    function handlePageSizeChange(e){
        handlers.setPageSize(e.target.value, false);
    }
</script>
<select on:change={handlePageSizeChange} name="page-size-options" class="fg-pagination-page-size-options" style="width: 61px; height: 23px;">
     {#each options as option}
     {#if option === pageSize}
        <option value={option} selected="selected">{option}</option>
     {:else}
        <option value={option}>{option}</option>
     {/if}
     {/each}
</select>
<div class="fg-pagination-options-helper-text">
    items per page 
</div>

