<script>
    import TooltipMaster from '../utils/tooltip/src/TooltipMaster';
    import {onMount} from 'svelte';
    export let tooltipRef, gridDimensions;
    let container;

    onMount(()=>{
        let tooltipMaster = new TooltipMaster();
        tooltipRef.tooltip = tooltipMaster.createTooltip({
            container,
            containerClasses: ['fg-tooltip-instance']
        });
        tooltipRef.tooltip.setBounds(gridDimensions);
    });
</script>
<div class="fg-tooltip-container" bind:this={container}>
</div>